import { useState } from 'react';

function Square() {
  const [value, setValue] = useState(''); // Initialize value to an empty string

  function handleClick() {
    // Update value based on your game logic, e.g.:
    setValue(value === '' ? 'X' : 'O'); // Toggle between X and O
  }

  return (
    <button
      className="square"
      onClick={handleClick}
      aria-label={`Square ${value}`} // Add ARIA label for accessibility
    >
      {value}
    </button>
  );
}

export default function Board() {
  return (
    <>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>

      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>

      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>
    </>
  );
}
